#include "pins_arduino.h"
#include "fabgl.h"
#include <canvas.h>
#define DOUBLEBUFFERING 1
#define scrWidht 800
#define scrHeight 600

gpio_num_t RPin = GPIO_NUM_13;
gpio_num_t GPin = GPIO_NUM_12;
gpio_num_t BPin = GPIO_NUM_14;
gpio_num_t HSPin = GPIO_NUM_27;
gpio_num_t VSPin = GPIO_NUM_26;

String ver = "TraceX1 3D APITraceX1.1 3D API(build:1.1.546)";
fabgl::VGA16Controller DisplayController;
Canvas cv(&DisplayController);
fabgl::Terminal Terminal;
void initScreenDriver() {
  DisplayController.begin(RPin, GPin, BPin, HSPin, VSPin);
  DisplayController.setResolution(VGA_640x350_70Hz, -1, -1, false);
  delay(100);
  Terminal.begin(&DisplayController);
  Terminal.enableCursor(true);
  Terminal.write("\e[2J");  //clear scr
  Terminal.write("\e[1;1H");
  Terminal.write("3D renderer Platform ESP32(8mb Psram) ver1.0 \n");
  Terminal.write("\e[2;1H");
  Terminal.write("TraceX Version:");
  Terminal.print(ver);
  delay(1500);
  Terminal.write("\e[3;1H");
  Terminal.write("main process-> task:");
  Terminal.write("nothing ");
  delay(2500);
  Terminal.write("\e[2J");
  Terminal.deactivate();
  DisplayController.setResolution(VGA_640x350_70Hz, -1, -1, DOUBLEBUFFERING);
}

